package de.themorpheus.edu.gateway.graphql.resolver.query.task;

import de.themorpheus.edu.backend.task.api.TaskGroupApi;
import de.themorpheus.edu.backend.task.invoker.ApiException;
import de.themorpheus.edu.backend.task.model.TaskGroup;
import de.themorpheus.edu.gateway.graphql.dto.task.TaskDTO;
import de.themorpheus.edu.gateway.graphql.dto.task.TaskGroupDTO;
import de.themorpheus.edu.gateway.graphql.resolver.query.user.UserResolver;
import graphql.kickstart.tools.GraphQLQueryResolver;
import graphql.schema.DataFetchingEnvironment;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;
import javax.validation.constraints.Min;
import java.util.LinkedList;
import java.util.List;

@Component
public class TaskGroupResolver implements GraphQLQueryResolver {

	@Autowired private TaskGroupApi taskGroupApi;
	@Autowired private LectureResolver lectureResolver;
	@Autowired private TaskResolver taskResolver;
	@Autowired private DifficultyResolver difficultyResolver;

	public TaskGroupDTO taskGroupById(@Min(1) int taskGroupId, DataFetchingEnvironment environment) throws ApiException {
		List<TaskDTO> taskDTOS = new LinkedList<>();
		TaskGroup taskGroup = taskGroupApi.getTaskGroup("SECRET", taskGroupId, "eyJhbGciOiJIUzUxMiIsInR5cCI6IkpXVCJ9..m3MuV-N7lkYn3SSamesEZ1o6FMrrMNQBXMv8JneSuoXu-5KgN2p7VMZJlvXN0_toKQQ1wixlGh-DxSO6pEfU_w");
		taskGroup.getTaskIds().forEach(taskId -> {
			try {
				taskDTOS.add(taskResolver.taskById(taskId, environment));
			} catch (ApiException e) {
				e.printStackTrace();
			}
		});
		return new TaskGroupDTO(
			taskGroup.getTaskGroupId(),
			taskGroup.getNameKey(),
			taskGroup.getLanguage(),
			lectureResolver.lectureById(taskGroup.getLectureId().getLectureId(), environment),
			difficultyResolver.difficultyById(taskGroup.getDifficultyId().getDifficultyId(), environment),
			taskDTOS.toArray(new TaskDTO[0]),
			UserResolver.EXAMPLE
		);
	}
}
